package com.example.recyclerview

import android.os.Bundle
import android.view.Menu
import android.view.MenuItem
import android.widget.Toast
import androidx.appcompat.app.AppCompatActivity
import com.example.recyclerview.adapter.ProvinceAdapter
import com.example.recyclerview.model.Tinh
import kotlinx.android.synthetic.main.activity_main.*

class MainActivity : AppCompatActivity() {

    private val provinceAdapter = ProvinceAdapter() {

    }
    private var data: MutableList<Tinh>? = mutableListOf(
        Tinh(
            "data:image/jpeg;base64," +
                    "m2kAL9evypUq1d+8XZWqmbu9prOYg5iv7WX8t6tDEKRpqCTuJ+hpUqxZ8SijNvT5WNgyvfsod1Htp9qH4uyg5kSYHP8qVKkY2NzU6ipW/RvOlSpU/kYihP/9k=",
            "Gia Lai",
            "3000",
            "" +
                    "Gia Lai là tỉnh có diện tích lớn thứ hai Việt Nam[3] và là một tỉnh cao nguyên nằm ở khu vực nửa trên của dãy 5 tỉnh tây " +
                    "nguyên Tây Nguyên (Gia Lai đứng thứ 2, đứng thứ 2 cả về dân số), miền Trung, Việt Nam."
        ),
        Tinh(
            "data:image/jpeg;base64," +
                    "m2kAL9evypUq1d+8XZWqmbu9prOYg5iv7WX8t6tDEKRpqCTuJ+hpUqxZ8SijNvT5WNgyvfsod1Htp9qH4uyg5kSYHP8qVKkY2NzU6ipW/RvOlSpU/kYihP/9k=",
            "Gia Lai",
            "3000",
            "" +
                    "Gia Lai là tỉnh có diện tích lớn thứ hai Việt Nam[3] và là một tỉnh cao nguyên nằm ở khu vực nửa trên của dãy 5 tỉnh tây " +
                    "nguyên Tây Nguyên (Gia Lai đứng thứ 2, đứng thứ 2 cả về dân số), miền Trung, Việt Nam."
        )
    )

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_main)
        initView()
        initData()
    }

    override fun onCreateOptionsMenu(menu: Menu?): Boolean {
        menuInflater.inflate(R.menu.menu, menu)
        return true
    }

    override fun onOptionsItemSelected(item: MenuItem): Boolean {
        val id = item.itemId
        when (id) {
            R.id.change_color ->
                return true
            R.id.about ->
                Toast.makeText(this, "App is a recycler view about provinces", Toast.LENGTH_LONG)
                    .show()
        }
        return super.onOptionsItemSelected(item)
    }

    private fun initView() {
        recyclerView.apply {
            adapter = provinceAdapter
            setHasFixedSize(false)
        }
    }

    private fun initData() {
        provinceAdapter.updataData(data)
    }
}
